import javax.servlet.annotation.WebFilter;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

/**
 * Global filter to apply security headers across all requests in the web application.
 * Protects against clickjacking, enforces HTTPS, and mitigates XSS vulnerabilities.
 */
@WebFilter(urlPatterns = "/*") // Apply to all incoming requests
public class GlobalSecurityHeadersFilter implements Filter {

    @Override
    public void init(FilterConfig filterConfig) throws ServletException {
        // No initialization required for this example
    }

    @Override
    public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain)
            throws IOException, ServletException {
        HttpServletResponse httpResponse = (HttpServletResponse) response;

        // 1. Prevent Clickjacking Attacks
        // Backward-compatible header: Block all external framing of this site
        httpResponse.setHeader("X-Frame-Options", "DENY");
        // Modern alternative (CSP): More flexible and supported by modern browsers
        httpResponse.setHeader("Content-Security-Policy", 
            "frame-ancestors 'none'; " +  // Block all framing attempts
            "default-src 'self'; " +       // Restrict all resources to same origin by default
            "script-src 'self' https://trusted-cdn.example.com; " +  // Allow scripts from trusted CDN
            "style-src 'self' 'unsafe-inline'; " +  // Allow inline styles (use cautiously; avoid if possible)
            "img-src 'self' data:;");       // Allow images from same origin and data URIs

        // 2. Enforce HTTPS with HSTS
        // Instruct browsers to use HTTPS for 1 year, include subdomains, and enable preload eligibility
        httpResponse.setHeader("Strict-Transport-Security", 
            "max-age=31536000; includeSubDomains; preload");

        // 3. Mitigate XSS Vulnerabilities
        // Enable browser's built-in XSS protection and block the page if an attack is detected
        httpResponse.setHeader("X-XSS-Protection", "1; mode=block");
        // Prevent MIME sniffing to avoid XSS via content type confusion
        httpResponse.setHeader("X-Content-Type-Options", "nosniff");

        // Pass the modified response to the next filter or servlet in the chain
        chain.doFilter(request, httpResponse);
    }

    @Override
    public void destroy() {
        // No cleanup required for this example
    }
}
