```java
// Fixed code snippet - Proper security headers implementation
protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
    // Prevent clickjacking by restricting frame embedding
    response.setHeader("X-Frame-Options", "DENY");

    // Enforce HTTPS by instructing browsers to use HTTPS for future requests
    response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

    // Mitigate XSS attacks by enabling Content Security Policy
    response.setHeader("Content-Security-Policy", 
        "default-src 'self'; script-src 'self'; style-src 'self'; object-src 'none';");

    // Prevent content type sniffing
    response.setHeader("X-Content-Type-Options", "nosniff");

    // Additional security headers for modern browsers
    response.setHeader("X-XSS-Protection", "1; mode=block");
    response.setHeader("Referrer-Policy", "strict-origin-when-cross-origin");

    response.setContentType("text/html");
    PrintWriter out = response.getWriter();
    out.println("<html><body>");
    out.println("<h1>Welcome to our website</h1>");
    out.println("</body></html>");
