// Example configuration that implements multiple security headers
protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
    // Prevent clickjacking by disallowing framing
    response.setHeader("X-Frame-Options", "DENY");

    // Enforce HTTPS by telling browsers to only use HTTPS for this domain
    response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

    // Mitigate XSS issues by enabling browser's built-in XSS protection
    response.setHeader("X-XSS-Protection", "1; mode=block");

    // Prevent browsers from MIME-sniffing a response away from the declared content type
    response.setHeader("X-Content-Type-Options", "nosniff");

    // Set a strong Content Security Policy to restrict resource loading
    response.setHeader("Content-Security-Policy", 
        "default-src 'self'; script-src 'self'; style-src 'self';");

    // Remove sensitive server information
    response.setHeader("Server", "");

    // Properly sanitize user input before including in response
    String userData = request.getParameter("data");
    String safeData = sanitizeHTML(userData);

    response.setContentType("text/html");
    PrintWriter out = response.getWriter();
    out.println("<html><body>");
    out.println("Data: " + safeData);
    out.println("</body></html>");
}

// Helper method to sanitize HTML content
private String sanitizeHTML(String input) {
    if (input == null) return "";
    return input.replaceAll("&", "&amp;")
                .replaceAll("<", "&lt;")
                .replaceAll(">", "&gt;")
                .replaceAll("\"", "&quot;")
                .replaceAll("'", "&#x27;");
}
