import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

// Servlet that explicitly sets security headers for individual endpoints
public class SecurePageServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
        // Prevent clickjacking by disallowing any framing of this page
        response.setHeader("X-Frame-Options", "DENY");

        // Enforce HTTPS for all future requests to the domain for 1 year, including subdomains
        response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

        // Enable browser XSS protection and block rendering if an attack is detected
        response.setHeader("X-XSS-Protection", "1; mode=block");

        // Stop browsers from guessing content types, reducing unintended execution risks
        response.setHeader("X-Content-Type-Options", "nosniff");

        // Restrict resource loading to trusted sources (customize values for your application)
        response.setHeader("Content-Security-Policy", "default-src 'self'; img-src 'self' https://trusted-cdn.com; script-src 'self'");

        // Write sample response content
        response.getWriter().write("Secure page with targeted security headers");
    }
}
