import javax.servlet.*;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

// Global filter to apply security headers to all outgoing responses
public class GlobalSecurityHeaderFilter implements Filter {
    @Override
    public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws IOException, ServletException {
        HttpServletResponse httpResponse = (HttpServletResponse) response;

        // Modern clickjacking protection using CSP (replaces X-Frame-Options for broader support)
        httpResponse.setHeader("Content-Security-Policy", "frame-ancestors 'none'");

        // Enforce long-term HTTPS with HSTS preload for browser inclusion in preload lists
        httpResponse.setHeader("Strict-Transport-Security", "max-age=63072000; includeSubDomains; preload");

        // Enable XSS protection with blocking mode
        httpResponse.setHeader("X-XSS-Protection", "1; mode=block");

        // Disable MIME type sniffing to prevent content type misinterpretation
        httpResponse.setHeader("X-Content-Type-Options", "nosniff");

        // Prevent caching of sensitive content to avoid data exposure via browser cache
        httpResponse.setHeader("Cache-Control", "no-store, no-cache, must-revalidate");

        // Continue processing the request through the application filter chain
        chain.doFilter(request, httpResponse);
    }

    @Override
    public void init(FilterConfig filterConfig) throws ServletException {
        // No initialization logic required for this filter
    }

    @Override
    public void destroy() {
        // No cleanup logic required for this filter
    }
}
