// Comprehensive security headers configuration to protect against common attacks
public void setSecurityHeaders(HttpServletResponse response) {
    // Prevents clickjacking by restricting which domains can embed the page
    response.setHeader("X-Frame-Options", "DENY");
    // Mitigates XSS attacks by restricting resource loading
    response.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self'");
    // Enforces HTTPS and secure cookie handling for all future requests
    response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");
    // Prevents MIME-type sniffing which could lead to script execution
    response.setHeader("X-Content-Type-Options", "nosniff");
    // Instructs browsers to stop using SSL/TLS protocols with known flaws
    response.setHeader("X-XSS-Protection", "1; mode=block");
    response.setContentType("text/html; charset=UTF-8");
    // Controls caching to avoid sensitive data storage in browser cache
    response.setHeader("Cache-Control", "no-store, no-cache, must-revalidate");
}
