import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

@WebServlet("/protected-resource")
public class ProtectedResourceServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
        // Block clickjacking by disallowing any framing of this page
        response.setHeader("X-Frame-Options", "DENY");

        // Force browsers to use HTTPS exclusively for the next year
        response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

        // Enable browser XSS protection and block malicious content immediately
        response.setHeader("X-XSS-Protection", "1; mode=block");

        // Prevent browsers from overriding declared content types
        response.setHeader("X-Content-Type-Options", "nosniff");

        // Restrict content sources to mitigate XSS and data injection risks
        response.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self'; img-src 'self' data:");

        // Write sample response content
        response.getWriter().write("<html><body>Protected Resource with Security Headers</body></html>");
    }
}
