import javax.servlet.*;
import javax.servlet.annotation.WebFilter;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

// Apply security headers to all incoming requests globally
@WebFilter("/*")
public class GlobalSecurityHeaderFilter implements Filter {
    @Override
    public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws IOException, ServletException {
        HttpServletResponse httpResponse = (HttpServletResponse) response;

        // Allow framing only from the same origin to support internal iframes safely
        httpResponse.setHeader("X-Frame-Options", "SAMEORIGIN");

        // Enforce HTTPS and include preload directive for browser preload lists
        httpResponse.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains; preload");

        // Add CSP header with strict sources to limit executable content
        httpResponse.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self' 'nonce-abc123'; style-src 'self'");

        // Set referrer policy to control how much referrer info is sent
        httpResponse.setHeader("Referrer-Policy", "strict-origin-when-cross-origin");

        // Disable caching of sensitive content
        httpResponse.setHeader("Cache-Control", "no-cache, no-store, must-revalidate");
        httpResponse.setHeader("Pragma", "no-cache");
        httpResponse.setHeader("Expires", "0");

        // Continue processing the request to the target resource
        chain.doFilter(request, response);
    }

    @Override
    public void init(FilterConfig config) throws ServletException {
        // No initialization logic required
    }

    @Override
    public void destroy() {
        // No cleanup logic required
    }
}
