import javax.servlet.http.HttpServletResponse;

// Method to set security headers in the HttpServletResponse with additional comments
public void setSecurityHeaders(HttpServletResponse response) {
    // Prevent clickjacking by setting the X-Frame-Options header to DENY
    response.setHeader("X-Frame-Options", "DENY");

    // Enforce HTTPS by setting the Strict-Transport-Security header
    // This header tells browsers to only connect via HTTPS for the specified duration
    response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

    // Mitigate XSS vulnerabilities by setting the Content-Security-Policy header
    // This header restricts the sources from which content can be loaded
    response.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self';");

    // Consider adding other security headers like X-Content-Type-Options and X-XSS-Protection
    response.setHeader("X-Content-Type-Options", "nosniff");
    response.setHeader("X-XSS-Protection", "1; mode=block");
}
