// Secure configuration with comprehensive security headers
@WebServlet("/api/data")
public class SecureServlet extends HttpServlet {
    protected void doGet(HttpServletRequest request, HttpServletResponse response) {
        // Prevent clickjacking attacks by restricting framing
        response.setHeader("X-Frame-Options", "DENY");

        // Enforce HTTPS and enable HSTS for one year
        response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

        // Mitigate XSS by implementing Content Security Policy
        response.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self'");

        // Block content type sniffing
        response.setHeader("X-Content-Type-Options", "nosniff");

        // Prevent XSS attacks through better XSS protection headers
        response.setHeader("X-XSS-Protection", "1; mode=block");

        // Set proper content type with charset
        response.setContentType("text/html; charset=UTF-8");

        // No caching of sensitive data
        response.setHeader("Cache-Control", "no-store, max-age=0");

        // Sanitize user input before rendering
        String userInput = sanitizeInput(request.getParameter("input"));
        response.getWriter().write("<div>" + userInput + "</div>");
    }

    private String sanitizeInput(String input) {
        if (input == null) return "";
        return input.replaceAll("<", "&lt;").replaceAll(">", "&gt;");
    }
}
