import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

@WebServlet("/protected-page")
public class ProtectedPageServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
        // Prevent clickjacking by disallowing any external framing of this page
        response.setHeader("X-Frame-Options", "DENY");
        // Enforce HTTPS for 1 year (31536000 seconds) across all subdomains
        response.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");
        // Block pages containing reflected XSS attacks instead of sanitizing
        response.setHeader("X-XSS-Protection", "1; mode=block");
        // Disable MIME sniffing to prevent content type spoofing attacks
        response.setHeader("X-Content-Type-Options", "nosniff");
        // Restrict resource loading to same origin to mitigate XSS and injection risks
        response.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self'; style-src 'self'");

        response.setContentType("text/html");
        response.getWriter().println("<html><body><h1>Secure Web Page</h1></body></html>");
    }
}
```

