// Servlet filter to set security headers for all responses
public class SecurityHeadersFilter implements Filter {

    @Override
    public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain)
            throws IOException, ServletException {

        HttpServletResponse httpResponse = (HttpServletResponse) response;

        // Prevent clickjacking by denying frame embedding
        httpResponse.setHeader("X-Frame-Options", "DENY");

        // Enforce HTTPS by instructing browsers to use HTTPS for all future requests
        httpResponse.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

        // Control MIME type sniffing to prevent certain attacks
        httpResponse.setHeader("X-Content-Type-Options", "nosniff");

        // Mitigate XSS vulnerabilities by enabling browser's built-in XSS protection
        // Note: This header has limited effectiveness and Content-Security-Policy is preferred
        httpResponse.setHeader("X-XSS-Protection", "1; mode=block");

        // Using Content-Security-Policy provides stronger XSS protection
        // This policy only allows scripts from the same origin and bans inline scripts
        httpResponse.setHeader("Content-Security-Policy", 
            "default-src 'self'; script-src 'self'; object-src 'none'");

        // Prevent browsers from caching sensitive pages
        httpResponse.setHeader("Cache-Control", "no-store, no-cache, must-revalidate");

        // Remove server information to limit information disclosure
        httpResponse.setHeader("Server", "");

        chain.doFilter(request, response);
    }
}
