import javax.servlet.annotation.WebFilter;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;

// Filter to apply security headers across all application requests
@WebFilter("/*")
public class GlobalSecurityHeaderFilter implements Filter {

    @Override
    public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain)
            throws IOException, ServletException {
        HttpServletResponse httpResponse = (HttpServletResponse) response;

        // Prevent clickjacking by disallowing framing of this site
        // Without this header, the application is vulnerable to clickjacking attacks
        httpResponse.setHeader("X-Frame-Options", "DENY");

        // Enforce HTTPS for all future requests (HSTS) for 1 year, including subdomains
        httpResponse.setHeader("Strict-Transport-Security", "max-age=31536000; includeSubDomains");

        // Enable browser XSS protection and block malicious content immediately
        httpResponse.setHeader("X-XSS-Protection", "1; mode=block");

        // Prevent MIME type sniffing to avoid XSS via content type spoofing
        httpResponse.setHeader("X-Content-Type-Options", "nosniff");

        // Restrict resource loading to trusted sources to mitigate XSS and data injection
        httpResponse.setHeader("Content-Security-Policy", "default-src 'self'; script-src 'self'; img-src 'self' data:;");

        // Control referrer information to prevent sensitive data leakage
        httpResponse.setHeader("Referrer-Policy", "strict-origin-when-cross-origin");

        // Pass the request through to the next filter or servlet
        chain.doFilter(request, response);
    }

    @Override
    public void init(FilterConfig filterConfig) throws ServletException {
        // No initialization needed for this filter
    }

    @Override
    public void destroy() {
        // No cleanup needed for this filter
    }
}
