import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.security.SecureRandom;
import java.util.Base64;

// Servlet demonstrating dynamic security header configuration for XSS mitigation
@WebServlet("/dynamic-secure-page")
public class DynamicSecureServlet extends HttpServlet {

    private static final SecureRandom secureRandom = new SecureRandom();

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException {
        // Generate a cryptographically secure nonce for inline script execution
        byte[] nonceBytes = new byte[16];
        secureRandom.nextBytes(nonceBytes);
        String nonce = Base64.getEncoder().encodeToString(nonceBytes);

        // Set Content-Security-Policy with nonce to allow trusted inline scripts
        // This blocks unauthorized inline scripts, a primary vector for XSS attacks
        response.setHeader("Content-Security-Policy",
                String.format("default-src 'self'; script-src 'self' 'nonce-%s'; style-src 'self' 'unsafe-inline';", nonce));

        // Extend HSTS duration for repeat visitors and enable preload submission
        response.setHeader("Strict-Transport-Security", "max-age=63072000; includeSubDomains; preload");

        // Override clickjacking protection to allow same-origin framing for this page
        response.setHeader("X-Frame-Options", "SAMEORIGIN");

        // Write HTML response with nonce-compliant inline script
        response.setContentType("text/html; charset=UTF-8");
        var writer = response.getWriter();
        writer.println("<html><head><title>Dynamic Secure Page</title></head><body>");
        // Inline script uses the generated nonce to comply with CSP rules
        writer.printf("<script nonce='%s'>document.getElementById('status').textContent = 'Secure and compliant!';</script>", nonce);
        writer.println("<h1>Dynamic Security Demo</h1>");
        writer.println("<p id='status'></p>");
        writer.println("</body></html>");
        writer.close();
    }
}
