import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.BufferedReader;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

@WebServlet("/safeReadFile")
public class SafeFileReaderServlet extends HttpServlet {
    // Restrict file access to this specific base directory
    private static final String ALLOWED_DIRECTORY = "/var/app/allowed_files/";

    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String filename = request.getParameter("filename");

        // Validate input presence
        if (filename == null || filename.isBlank()) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Filename parameter is required");
            return;
        }

        try {
            // Resolve user input against the allowed directory and normalize the path
            Path basePath = Paths.get(ALLOWED_DIRECTORY).toRealPath();
            Path resolvedPath = basePath.resolve(filename).normalize().toRealPath();

            // Ensure the resolved path stays within the allowed directory to block traversal
            if (!resolvedPath.startsWith(basePath)) {
                response.sendError(HttpServletResponse.SC_FORBIDDEN, "Access to this file is restricted");
                return;
            }

            // Safely read and output the file content using NIO.2 API
            response.setContentType("text/plain");
            try (BufferedReader reader = Files.newBufferedReader(resolvedPath)) {
                String line;
                while ((line = reader.readLine()) != null) {
                    response.getWriter().write(line + "\n");
                }
            }
        } catch (IOException e) {
            response.sendError(HttpServletResponse.SC_NOT_FOUND, "File not found");
        }
    }
}
