import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

public class SecureFileDownloadServlet extends HttpServlet {
    // Base directory for stored documents, resolved to an absolute path
    private static final Path BASE_DOC_DIR = Paths.get("/var/web/documents/").toAbsolutePath().normalize();

    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        // Validate presence of the filename parameter
        String fileName = request.getParameter("file");
        if (fileName == null || fileName.isBlank()) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Missing or invalid file name");
            return;
        }

        // Resolve the requested file path against the base directory
        Path requestedFilePath = BASE_DOC_DIR.resolve(fileName).normalize();

        // Ensure the resolved path stays within the intended directory structure
        if (!requestedFilePath.startsWith(BASE_DOC_DIR)) {
            response.sendError(HttpServletResponse.SC_FORBIDDEN, "Requested file is not accessible");
            return;
        }

        // Verify the path points to an existing regular file
        if (!Files.exists(requestedFilePath) || !Files.isRegularFile(requestedFilePath)) {
            response.sendError(HttpServletResponse.SC_NOT_FOUND, "File not found");
            return;
        }

        // Stream the file content to the client using secure NIO methods
        response.setContentType("application/octet-stream");
        response.setHeader("Content-Disposition", "attachment; filename=\"" + requestedFilePath.getFileName() + "\"");
        Files.copy(requestedFilePath, response.getOutputStream());
    }
}
