import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;

public class SecureAuthentication {
    public static boolean authenticate(Connection dbConn, String username, String password) throws SQLException {
        // Parameterized query separates SQL logic from user input to prevent SQL injection
        String sql = "SELECT * FROM users WHERE username = ? AND password = ?";

        // PreparedStatement safely handles user input by using placeholders
        PreparedStatement pstmt = dbConn.prepareStatement(sql);
        pstmt.setString(1, username); // Bind first parameter (index starts at 1)
        pstmt.setString(2, password); // Bind second parameter

        ResultSet result = pstmt.executeQuery();
        boolean isAuthenticated = result.next();

        // Clean up resources
        result.close();
        pstmt.close();
        return isAuthenticated;
    }
}
